let hamburger = document.querySelector('.hamburger');
let header = document.querySelector('.header');
let comments = document.querySelector('.comments-area');
let sharecontent = document.querySelector('.share-content');
let articlestart = document.querySelector('.articlestart');

if (articlestart) {
	var toparticle = (articlestart.getBoundingClientRect().top)+30+'px'
	sharecontent.style.top  = toparticle;
}

hamburger.addEventListener('click', () => {
	hamburger.classList.toggle('active');
	document.body.classList.toggle('nav-open');
});

window.addEventListener('scroll', function(e) {
	var scroll = window.scrollY;
	if (scroll > 100) {
		header.classList.add('mini');header.classList.remove('maxi');
	}
	else {
		header.classList.remove('mini');header.classList.add('maxi');
	}

	if (articlestart) {
		if (articlestart.getBoundingClientRect().top < 0) {
			sharecontent.style.top = '100px';
		}
		else {
			if(articlestart.getBoundingClientRect().top < 350) {
				sharecontent.style.top = (articlestart.getBoundingClientRect().top)+30+'px';
			}
			else {
				sharecontent.style.top = toparticle;
			}
		}
	}

	//console.log(comments.getBoundingClientRect().top)
	if (comments) {
		
		var showcomments = comments.getBoundingClientRect().top-400
		if (showcomments > 0) {sharecontent.classList.add('share-show');sharecontent.classList.remove('share-hide');}
		else {sharecontent.classList.remove('share-show');sharecontent.classList.add('share-hide');}

		//if (scroll < 400) {sharecontent.classList.remove('share-show');sharecontent.classList.add('share-hide');}
	}


});

var TxtRotate = function(el, toRotate, period) {
	this.toRotate = toRotate;
	this.el = el;
	this.loopNum = 0;
	this.period = parseInt(period, 10) || 2000;
	this.txt = '';
	this.tick();
	this.isDeleting = false;
};

TxtRotate.prototype.tick = function() {
	var i = this.loopNum % this.toRotate.length;
	var fullTxt = this.toRotate[i];

	if (this.isDeleting) {
		this.txt = fullTxt.substring(0, this.txt.length - 1);
	} else {
		this.txt = fullTxt.substring(0, this.txt.length + 1);
	}

	this.el.innerHTML = '<span class="wrap">'+this.txt+'</span>';

	var that = this;
	var delta = 100 - Math.random() * 100;

	if (this.isDeleting) { delta /= 2; }

	if (!this.isDeleting && this.txt === fullTxt) {
		delta = this.period;
		this.isDeleting = true;
	} else if (this.isDeleting && this.txt === '') {
		this.isDeleting = false;
		this.loopNum++;
		delta = 900;
	}

	setTimeout(function() {
		that.tick();
	}, delta);
};

window.onload = function() {
	var elements = document.getElementsByClassName('txt-rotate');
	for (var i=0; i<elements.length; i++) {
		var toRotate = elements[i].getAttribute('data-rotate');
		var period = elements[i].getAttribute('data-period');
		if (toRotate) {
		new TxtRotate(elements[i], JSON.parse(toRotate), period);
		}
	}
};


